import './App.css';

import React, { useState, useEffect } from 'react';
import { Form, Input, Button, Checkbox } from 'antd';
const formItemLayout = {
  labelCol: {
    span: 4,
  },
  wrapperCol: {
    span: 8,
  },
};
const formTailLayout = {
  labelCol: {
    span: 4,
  },
  wrapperCol: {
    span: 8,
    offset: 4,
  },
};

function App() {
  const [form] = Form.useForm();
  const [ExpDate] = useState(false);
  useEffect(() => {
    form.validateFields(['ExpDate']);
  }, [ExpDate]);


  const onCheck = async () => {
    try {
      const values = await form.validateFields();
      console.log('Success:', values);
       await fetch('http://localhost:3000/payment', {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(values)
        })
    } catch (errorInfo) {
      console.log('Failed:', errorInfo);
    }
  };

  return (
      <Form form={form} name="dynamic_rule">
        <Form.Item
            {...formItemLayout}
            name="CardNumber"
            label="CardNumber"
            rules={[
              {
                required: true,
                message: 'Please input CardNumber',
              },
            ]}
        >
          <Input placeholder="Please CardNumber" />
        </Form.Item>
        <Form.Item
            {...formItemLayout}
            name="ExpDate"
            label="ExpDate"
            rules={[
              {
                required: true,
                message: 'Please input ExpDate',
                  min: 16,
                  max:16
              },
            ]}
        >
          <Input placeholder="Please ExpDate" />
        </Form.Item>

          <Form.Item
            {...formItemLayout}
            name="Cvv"
            label="Cvv"
            rules={[
              {
                required: true,
                message: 'Please input Cvv',
              },
            ]}
        >
          <Input placeholder="Please Cvv" />
        </Form.Item>
          <Form.Item
            {...formItemLayout}
            name="Amount"
            label="Amount"
            rules={[
              {
                required: true,
                message: 'Please input Amount',
              },
            ]}
        >
          <Input placeholder="Please Amount" />
        </Form.Item>

        <Form.Item {...formTailLayout}>
          <Button type="primary" onClick={onCheck}>
            Check
          </Button>
        </Form.Item>
      </Form>
  );
};



export default App;
